INSERT INTO `product` (`productid`, `product_name`, `thumbnail`, `Description`, `idcategory`, `author`, `publish`) VALUES
(1, 'Cho Tôi Xin Một Vé Đi Tuổi Thơ', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180164_1_43_1_57_1_4_1_2_1_210_1_29_1_98_1_25_1_21_1_5_1_3_1_18_1_18_1_45_1_26_1_32_1_14_1_1233.jpg', 'Trên mạch kể hấp dẫn của câu chuyện, ngược dòng thời gian, tác giả đã đưa người đọc trở lại những năm tháng tuổi thơ vui vẻ với cốt truyện xoay quanh 4 bạn nhỏ nghịch ngợm, hồn nhiên cu Mùi, Hải cò, Tí sún và con Tủn. Trong thế giới tuổi thơ tươi đẹp ấy của những đứa trẻ, chúng không hề có những lo toan, bộn bề về cuộc sống vật chất và tinh thần mà chỉ hạnh phúc đắm chìm vào những trò chơi, những vui đùa của tuổi nhỏ. Trải dài trên từng trang viết là những câu chuyện hài hước, dí dỏm, những trò đùa vui vẻ khiến độc giả thực sự ước ao được quay lại những cảm xúc trong sáng, quãng thời gian vô tư như vậy một lần nữa.', 116, 'Nguyễn Nhật Ánh', '2008'),
(2, 'Tôi là Bêtô', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180164_1_43_1_57_1_4_1_2_1_210_1_29_1_98_1_25_1_21_1_5_1_3_1_18_1_18_1_45_1_26_1_32_1_14_1_1236.jpg', 'Sách Tôi là Bêtô là những mẩu chuyện của chú cún Bêtô với bạn của chú là  Binô, cả hai cùng sống với cô chủ Ni, một bạn mới là Laica ở nơi khác. Với cách nhìn, cách nghĩ và ứng xử theo suy nghĩ của chú cún, người đọc truyện Tôi là Bêtô sẽ nhìn nhận ra, phân biệt tốt xấu trong thế giới con người, những cảm nhận hồn nhiên trong trẻo theo tầm nhìn của Bêtô, rồi tình bạn của cặp Bêtô - Binô, phù hợp với suy nghĩ của các bạn ở tuổi thiếu niên đang chuẩn bị trưởng thành làm người lớn.', 116, 'Nguyễn Nhật Ánh', '2007'),
(3, 'Rừng Nauy', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_240307.jpg', 'Rừng Na-Uy là tiểu thuyết của nhà văn Nhật Bản Murakami Haruki, là dòng hồi tưởng của nhân vật chính là chàng sinh viên bình thường Watanabe Tōru.\r\n\r\nTác phẩm này đã đưa Murakami lên thành một trong những nhà văn hàng đầu của Nhật Bản. Cậu ta đã trải qua nhiều cuộc tình chớp nhoáng với nhiều cô gái trẻ ưa tự do. Nhưng cậu ta cũng có những mối tình sâu nặng, điển hình là với Naoko, người yêu của người bạn thân nhất của cậu, một cô gái không ổn định về cảm xúc, và với Midori, một cô gái thẳng thắn và hoạt bát.', 116, 'Haruki Murakami', '1987'),
(4, 'Tôi Thấy Hoa Vàng Trên Cỏ Xanh', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180164_1_43_1_57_1_4_1_2_1_210_1_29_1_98_1_25_1_21_1_5_1_3_1_18_1_18_1_45_1_26_1_32_1_14_1_2199.jpg', 'Nội dung truyện nói vè tuổi thơ nghèo khó của hai anh em Thiều và Tường ở một làng quê Việt thân thuộc và nên thơ. Là nơi đã chứng kiến những rung động đầu đời của cả hai, tình cảm gia đình, tình anh em yêu thương chân thành, cũng như những đố kỵ, ghen tuông và những nỗi đau trong veo trong quá trình trưởng thành.', 116, 'Nguyễn Nhật Ánh', '2010'),
(5, 'Làm Bạn Với Bầu Trời', 'https://cdn0.fahasa.com/media/catalog/product/u/n/untitled-2_44.jpg', 'Bầu trời tượng trưng cho sự rộng mở, khoáng đạt. Cũng vì thế, “Làm bạn với bầu trời” sẽ giúp con người bao dung và luôn có những suy nghĩ trong trẻo. Với thông điệp này, Nguyễn Nhật Ánh đã viết nên một truyện dài, dồn hết bút lực làm bật lên một trái tim ngập tràn yêu thương nên đã luôn tìm thấy cơ hội để tha thứ cho cuộc đời, nhờ vậy mà tâm hồn ấy lúc nào cũng bình yên. Áng văn như ngọn gió thổi bùng khát khao được sống và sống thật tốt mỗi phút giây.', 116, 'Nguyễn Nhật Ánh', '2019'),
(6, 'Con Chim Xanh Biếc Bay Về', 'https://cdn0.fahasa.com/media/catalog/product/b/i/biamem.jpg', 'Nhẹ nhàng mà tinh tế, sâu lắng chính là những gì mà người đọc thấy được qua tác phẩm này của Nguyễn Nhật Ánh. Ta bắt gặp trong những câu chuyện tình yêu, sự bi thương, đau khổ, chia lìa đến cảm động rơi nước mắt. Nhưng tình yêu thì cũng có vô số loại. “Mấy ai cắt nghĩa được tình yêu?” (Xuân Diệu). Thế nên câu chuyện tình yêu trong “Con chim xanh biếc bay về” cũng có phần khác. Có thể nói rằng đây là một mối tình giữ trong im lặng, dù mọi người đều biết, chính bản thân nhân vật cũng biết nhưng không dám thừa nhận. Để rồi một ngày khi vô tình trong buổi ra mắt, Khuê mới biết Sâm là người mà hai bên gia đình sắp đặt. Nhưng chỉ vì một vài chuyện xảy ra mà Khuê không đồng ý cuộc hôn nhân này. Sau này, khi mọi chuyện tưởng như chấm dứt thì sợi dây khúc mắc giữa hai người được tháo gỡ, họ trở về bên nhau. Qua giọng văn đầy sâu sắc của tác giả, có lẽ mỗi chúng ta sẽ thấy một phần của chính mình ở trong đó.', 116, 'Nguyễn Nhật Ánh', '2020'),
(7, '999 Lá Thư Gửi Cho Chính Mình', 'https://cdn0.fahasa.com/media/catalog/product/9/9/999lathu-taibbb1_2.jpg', 'Cầm trên tay cuốn sách “999 lá thư gửi cho chính mình” – bạn sẽ hiểu rằng: tuổi trẻ của chúng ta dù có mong manh đến đâu thì cũng sẽ thành công vượt qua mọi khó khăn một cách mạnh mẽ ngoài sức tưởng tượng. Một ngày nào đó, bạn sẽ cảm nhận được hạnh phúc, tự tin của chính bản thân và học được cách mỉm cười trước những nỗi đau của quá khứ.', 116, 'Nguyễn Nhật Ánh', '2018'),
(8, 'Có Hai Con Mèo Ngồi Bên Cửa Số', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8934974178378.jpg', 'Cuốn sách giúp ta thấy tình yêu có thể làm nên những điều tuyệt diều, có thể thay đổi những điều tưởng chừng là không thể. Đọc xong “Hai con mèo ngồi bên cửa sổ” không chỉ thấy tình yêu mà còn có một thứ gì đó nhẹ nhàng, man mát và bình yên ở trong lòng – nhẹ và mát như âm thanh của một chiếc lông rơi xuống thềm nhà.', 116, 'Nguyễn Nhật Ánh', '2012'),
(9, 'Chuyện Con Mèo Và Con Chuột Bạn Thân Của Nó', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_181813.jpg', 'Mèo và chuột vốn là hai con vật “không đội trời chung” với nhau. Xưa nay, loài chuột chỉ cần nhìn thấy mèo là chạy bán sống, bán chết. Mấy chú mèo cũng chẳng kém cạnh, hễ nhác thấy bóng chuột là “đuổi cùng giết tận” chứ chẳng tha.\r\n\r\nCòn trong mắt con người, chuột là con vật gặm nhấm đáng ghét. Con người có thể làm bạn với mèo, nhưng không đời nào kết thân với chuột. Luis Sepúlveda đã làm thay đổi tất cả những định kiến đó bằng câu chuyện ngụ ngôn đầy cảm động Chuyện con mèo và con chuột bạn thân của nó.', 116, 'Luis Sepúlveda', '2016'),
(10, 'Không gia đình', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935230009474_1.jpg', 'Không gia đình kể chuyện một em bé không cha mẹ, không họ hàng thân thích, đi theo một đoàn xiếc chó, khỉ, rồi cầm đầu đoàn ấy đi lưu lạc khắp nước Pháp, sau đó bị tù ở Anh, cuối cùng tìm thấy mẹ và em. Em bé Rêmi ấy đã lớn lên trong gian khổ. Em đã chung đụng với mọi hạng người, sống khắp mọi nơi, \"nơi thì lừa đảo, nơi thì xót thương\". Em đã lao động mà sống, lúc đầu dưới quyền điều khiển của một ông già từng trải và đạo đức, cụ Vitali, về sau thì tự lập và không những lo cho mình, còn bảo đảm việc biểu diễn và sinh sống cho cả một gánh hát rong. Đã có khi em và cả đoàn lang thang mấy hôm liền không có chút gì trong bụng. Đã có khi em suýt chết rét. Đã có khi em bị lụt ngầm chôn trong giếng mỏ mười mấy ngày đêm. Đã có khi em mắc oan bị giải ra trước tòa và bị ở tù.\r\n\r\nVà cũng có khi em được nuôi nấng đàng hoàng, no ấm. Nhưng dù ở đâu, trong cảnh ngộ nào, em vẫn noi theo nếp rèn dạy của ông già Vitali giữ phẩm chất làm người, nghĩa là ngay thẳng, gan dạ, tự trọng, thương người, ham lao động, không ngửa tay xin xỏ, không dối trá, gian giảo, nhớ ơn nghĩa, luôn luôn muốn làm người có ích...', 116, 'Hector Malot', '1878'),
(11, 'Có Một Ngày, Bố Mẹ Sẽ Già Đi', 'https://cdn0.fahasa.com/media/catalog/product/c/o/co-mot-ngay-bo-me-se-gia-di.jpg', 'Càng lớn lên, những quyến luyến của chúng ta đối với bố mẹ càng ít. Khi đại bàng non có thể giương cánh, nó luôn hướng tới không trung, chứ không phải rúc vào đôi cánh bố mẹ. Bạn khát vọng cuộc sống khởi sắc, không còn quẫn bách giật gấu vá vai. Bạn khát vọng sự nghiệp tiến triển, như thế mới không uổng bao năm vất vả tăng ca bất kể gió mưa. Bạn khát vọng con cái của mình về sau có thể lớn lên vui vẻ khỏe mạnh, đạt được thành tích đáng tự hào, vươn lên trong môi trường cạnh tranh quyết liệt… Nhưng bạn lại vô tình đặt một khát vọng khác nằm sâu trong tâm hồn xuống vị trí dưới cùng, khát vọng này chính là hy vọng bố mẹ đừng vội già đi. Giống như mỗi ngày đều có thể nhìn thấy mặt trời, vào những ngày trời quang nắng đẹp, bạn cảm thấy ấm áp và dễ chịu, nó như một thứ đương nhiên nên có, hưởng thụ sự tốt đẹp nó mang lại đã trở thành thói quen của bạn. Nhưng nếu một ngày, có người nói với bạn mặt trời sẽ không mọc nữa, bạn cảm thấy thế nào? Có lẽ phần lớn mọi người đều cảm thấy sợ hãi và luống cuống. Bố mẹ cũng giống như mặt trời, luôn lặng lẽ ở sau lưng, cho bạn chỗ dựa và sự ấm áp, nhưng nếu một ngày bố mẹ không còn ở đấy nữa, cảm giác an toàn quen thuộc cũng lập tức biến mất, bấy giờ muốn tìm lại, sợ rằng đã quá muộn. Bởi vì năm tháng vẫn mãi trôi, thời gian không trở lại, tên siêu trộm thời gian đang dần dần lấy đi tuổi xuân của bố mẹ, rồi có một ngày, bố mẹ sẽ già đi.', 126, 'Hơn 20 tác giả', '2020'),
(12, 'Mình Ngồi Xuống Kể Tổn Thương Trong Lòng', 'https://cdn0.fahasa.com/media/catalog/product/m/_/m_nh-ng_i-xu_ng-k_-t_n-th_ng-trong-l_ngbia-1.jpg', '“Mình Ngồi Xuống Kể Tổn Thương Trong Lòng” là những dòng văn, câu thơ trải lòng về cuộc đời, tình yêu, cách chúng ta đối diện với người và với chính bản thân mình, cách một người cô đơn ngồi xuống giữa thế gian tấp nập, trải lòng mình ra, cẩn trọng ghép nối từng mảnh vỡ để tự chữa lành.\r\n\r\nCuốn sách thơ – tản văn ra mắt vào dịp cuối năm 2021 chắc chắn sẽ đem lại cho bạn đọc những chiêm nghiệm đa chiều, những góc nhìn mới mẻ, những sự bất ngờ thú vị tới từ một Minh Dự rất khác, không ồn ào mà đầy suy tư, không chọc cười mà lẳng lặng lau đi những giọt nước mắt bấy lâu nay cất giấu thật sâu trong lòng. Cuốn sách hứa hẹn sẽ được độc giả trẻ khắp nơi đón nhận và trở thành hiện tượng xuất bản mới của năm nay.', 126, 'Minh Dự', '2022'),
(13, 'Chúng Ta Rồi Sẽ Hạnh Phúc, Theo Những Cách Khác Nhau', 'https://cdn0.fahasa.com/media/catalog/product/1/3/139990755_1302883240068333_3844579474690419303_n.jpg', 'Bạn đã bao giờ rơi vào trạng thái mệt mỏi đến tột độ, bạn không còn tin vào hạnh phúc trên đời này chưa? Tuổi trẻ của chúng ta là những tháng ngày như thế, có lúc chúng ta đánh mất niềm tin vào hạnh phúc. Nếu bạn đã và đang chưa biết cách cân bằng cảm xúc thì hãy đọc cuốn sách “Chúng ta rồi sẽ hạnh phúc theo những cách khác nhau”.', 126, 'Thảo Thảo', '2021'),
(14, 'Gửi Bạn, Người Đã Kiệt Sức Vì Những Chịu Đựng Âm Thầm', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_219045.jpg', 'Nếu bạn có ai đó để nói ra những khó chịu trong lòng thì thật tốt, hãy nói ra, bạn sẽ cảm thấy khá hơn. Còn nếu bạn cảm thấy việc nói ra là một gánh nặng, bạn không thoải mái khi phải vạch ra cho người khác xem từng chút một khó khăn của mình và sợ hãi việc phải nghe người khác cho lời khuyên, thì những uất ức ấy cứ giữ cho riêng mình, không sao cả. Có những người thích một mình, dò dẫm từng bước một, giải quyết những rắc rối của chính mình, không cần cũng không muốn bất kì ai can dự vào.\r\nViệc bạn đối xử với chính mình ra sao sẽ quyết định thái độ của người khác với bạn. Người không biết tôn trọng chính mình là đáng trách nhất, chúng ta không biết yêu lấy mình, giữa thế gian vội vã này, liệu có mấy người đủ nhẫn nại để ở bên, cảm thông và yêu thương chúng ta?', 126, 'Geulbaewoo', '2021'),
(15, 'Mình Phải Sống Như Biển Rộng Sông Dài', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935325004438.jpg', 'Chẳng biết bạn đã từng trải qua cảm giác này chưa… Rằng thứ mà bạn coi trọng như sinh mệnh, trong mắt người khác lại chẳng đáng mấy đồng?\r\n\r\nCó vô số kiểu thành công: Thành công về vật chất. Thành công về tri thức, về thể chất, về tầm ảnh hưởng, cảm xúc hay tinh thần… Biết bao nhiêu khía cạnh đa dạng của cuộc sống. Nhưng người đời vốn thiếu lòng kiên nhẫn, chỉ đọc được đến “vật chất” là ngừng.\r\nCùng một câu nói, cùng một lý lẽ, nhưng tài khoản của bạn phải có vài tỉ thì người ta mới chịu tin. Thật lạ lùng!\r\n\r\nVậy cuối cùng bạn sẽ lựa chọn thế nào? Bước vào trường đua để vượt lên trên tất cả, sau đó ngoảnh lại cùng nụ cười khinh khi, cao ngạo, hay cứ thế bỏ ngoài tai mọi lời chế nhạo, bình tĩnh rẽ sang lối nhỏ, âm thầm tận hưởng thế giới của riêng mình?', 126, 'Xu', '2022'),
(16, 'Tạm Biệt Tôi Của Nhiều Năm Về Trước', 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a-1_tam-biet-toi-cua-nhieu-nam-ve-truoc.jpg', 'Tạm biệt tôi của nhiều năm về trước là cuốn sách dành riêng cho những ai đã và đang trưởng thành. Gửi một lời tạm biệt cho quá khứ, cho những chuyện đã qua để nắm lấy những bình yên đang đến.\r\nNếu bạn là người đã từng chịu nhiều mất mát, đau thương trong quá khứ. Đã từng đổ vỡ, từng gặp khó khăn thì đây sẽ là cuốn sách ủi an tâm hồn bạn. Bạn sẽ như chính nhân vật trong sách, cùng đồng cảm và thấu hiểu với nội dung cuốn sách mang lại. Cuốn sách Tạm biệt tôi của nhiều năm về trước không quá dày, chỉ vỏn vẹn 248 trang với 235 câu quotes về cuộc sống, gia đình, bạn bè, công việc, tình yêu. Nếu bạn đã từng đọc qua cuốn 999 Lá thư gửi cho chính mình thì chắc chắn cuốn sách này bạn sẽ cực kỳ yêu thích.', 126, 'An', '2021'),
(17, 'Thế Giới Này Âm Thầm Yêu Em', 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia-1_the-gioy-am-tham-yeu-em.jpg', 'Ông Trời chưa bao giờ vứt bỏ mỗi một linh hồn cố gắng sinh trưởng, cũng chưa từng phụ bạc mỗi một sinh mệnh đi lướt qua nhau. Tất cả ấm áp không mong mà gặp đang lặng lẽ thay đổi những cuộc đời tưởng chừng ảm đạm hỗn độn ấy. Tôi biết tôi có thể sống rất tốt, tại đây, một góc trong thế giới lạnh lùng này.\r\nNhững tháng ngày khó khăn gian khổ ấy, sẽ vun vén một bát cơm, một chén trà, một chiếc giường, một chùm tia sáng, và một vài tấm chân tình.', 126, 'Cinderella', '2021'),
(18, 'Cứ Khóc, Cứ Tan Vỡ Và Trưởng Thành', 'https://cdn0.fahasa.com/media/catalog/product/c/u/cu-khoc-cu-tan-vo-va-tuong-thanh-bia-1-01.jpg', 'Cuốn sách này được viết cho những người đang trầy trật đấu tranh với chính mình mỗi ngày hay đang có những vấn đề tâm lý. Các bạn đều là những người thật đặc biệt theo nhiều cách khác nhau, mang trên mình những rạn nứt khác nhau. Những vết nứt đó chỉ có bạn mới biết nó làm bạn đau đến mức nào. Và cũng chính bạn mới có thể tìm cách để hàn gắn nó ra sao.\r\n“Một quyển sách chạm tới nơi sâu thẳm nhất của những tâm hồn đang hoang hoải, đang mông lung trong hàng mớ những câu hỏi “nỗi đau của tôi ai thấu hiểu?”, “tôi đang trải qua điều gì thế này?”, “tôi sẽ phải đối diện với nỗi đau như thế nào?”, “tôi phải đi tiếp con đường chênh vênh này ra sao?”.', 126, 'Yến Nhi', '2020'),
(19, 'Tôi Thích Bản Thân Nỗ Lực Hơn', 'https://cdn0.fahasa.com/media/catalog/product/6/0/600bia-toi-thich-ban-than-no-luc-hon-pr-05.jpg', '“Một là không có bối cảnh, hai là không có tiền, ba là không có mối quan hệ, chỉ có thể trông cậy vào chính mình”. Nếu bạn đã từng có ý nghĩ như vậy, thì một cuốn sách như “Tôi thích bản thân nỗ lực hơn” chính là tiếng lòng dành cho bạn.\r\n\r\nBạn có từng bị ngăn cấm, bị coi là gàn dở khi từ bỏ một công việc “ ổn định, nhàn hạ” để theo đuổi thứ gọi là “đam mê” mà bạn chấp nhận đánh đổi bằng bất cứ giá nào? Thất bại ư? Nhưng nếu bạn thực sự thành công như tác giả của cuốn sách này', 126, 'Chu Xung', '2022'),
(20, 'Bình Thản Đối Diện, Nhẹ Nhàng Bước Qua', 'https://cdn0.fahasa.com/media/catalog/product/1/9/191ad89ed52f760cae09191072385879.jpg', 'Cuốn sách mới nhất của tác giả Du Phong là những đoạn đường thanh xuân, là những câu chuyện trong đó có dáng hình của ai đó – một người chúng ta đã từng bỏ lỡ. Đối diện với chia tay là tận cùng đau khổ nhưng lật giở từng trang sách chúng ta sẽ nhận ra rằng: Thay bằng cách trốn tránh hiện thực, chúng ta nhẹ nhàng đối diện để rồi có thể nhìn lại quá khứ ấy một cách đẹp đẽ, những nỗi buồn ấy, những con người ấy là duyên nợ khiến chúng ta buộc phải trải qua một lần trong đời!', 126, 'Du Phong', '2021'),
(21, 'Thiên Quan Tứ Phúc', 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia-mem_tqtp5.jpg', 'Bị đày xuống nhân gian để trừ ma, vị thần tiên nọ gặp phải một con quỷ và nhanh chóng phát hiện bí mật đen tối phía sau những vị thần chốn thiên đình.', 136, 'Mặc Hương Đồng Khứu', '2022'),
(22, 'All In Love - Ngập Tràn Yêu Thương', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_38340.jpg', 'Truyện tình yêu này xem chừng có chút phúc hắc, bá đạo, xem chừng lại có chút đáng yêu, lại có chút mê đắm. Từ Vi Vũ tính hơi hơi ưa sạch, da mặt cũng hơi hơi dày, ngoài ra còn hơi hơi bỉ ổi, nhưng anh trước mặt mọi người luôn luôn là một Từ Vi Vũ trăng thanh gió mát, tách biệt xa lạ, trong kiêu ngạo có lạnh lùng, trong lạnh lùng có xa cách, trong xa cách có cao sang.', 136, 'Cố Tây Tước', '2020'),
(23, 'Từng Có Người Yêu Tôi Như Sinh Mệnh', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_41937.jpg', 'Thời còn trẻ, chúng ta thường không hiểu thế nào là tình yêu. Lúc mới bước vào đời, tôi từng nghĩ tình yêu có thể vượt qua tất cả. Khi đó tôi không hề biết trên đời này còn tồn tại một sức mạnh gọi là số phận, chúng ta chỉ có thể chấp nhận mà không thể thay đổi. Lúc ở trong phòng tắm của trường học và cất cao tiếng hát “I love you more than I can say”, tôi không hề ngờ rằng, câu chuyện đó sẽ có một ngày xảy ra với tôi. Tôi gặp anh lần đầu tiên là ở một tình huống đẫm máu tại thành phố Odessa, đất nước Ukraine...', 136, 'Thư Nghi', '2020'),
(24, 'Bảy Năm Vẫn Ngoảnh Về Phương Bắc (Bộ 3 Tập: 3,4,5)', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936186541377.jpg', 'Lục Bắc Thần luôn khiến cô chìm vào ảo giác. Bóng hình thân thuộc, gương mặt thân thuộc, rồi khiến cô không sao thở nổi.\r\nNhưng anh lại nói: “Đã phụ lòng người, vì cớ gì phải đau khổ?”\r\nBỗng một ngày nào đó, có người nói với Cố Sơ: Đừng có tin Lục Bắc Thần, vì anh ta, không phải Lục Bắc Thần…\r\nTình yêu bị tuổi xuân giết chết, còn tuổi xuân lại bị tình yêu lưu đày…\r\nTruyện có những tình tiết ngọt ngào về một tình yêu ấp ủ nồng cháy. Mời bạn đọc cùng thưởng thức truyện ngôn tình để hiểu rõ hơn về Cố Sơ và Lục Băc Thần.', 136, 'Ân Tầm', '2019'),
(25, 'Động Phòng Hoa Chúc Cách Vách', 'https://cdn0.fahasa.com/media/catalog/product/2/d/2d_81a3bd42598d4926934ae5c20a015663_master.jpg', 'Nếu cô tin tưởng, lời nói “anh yêu em” là sự thật…\r\n\r\nNếu cô tin tưởng, những tổn thương anh mang đến cho cô không phải là anh cố ý….\r\n\r\nNếu cô tin tưởng, những nỗi đau mà anh không thể nói thành lời tựa như những tế bào độc bệnh lan truyền toàn thân….\r\n\r\nThì liệu cô và anh có bỏ lỡ khoảng thời gian ba năm của cuộc đời???', 136, 'Diệp Lạc Vô Tâm', '2019'),
(26, 'Em Là Vì Sao Trong Mắt Anh', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_77.jpg', 'Một người hờ hững một người lại đau khổ.\r\nMột người trung thành một người lại dối lừa\r\nMột người đã cho đi tất cả một người lại cướp đoạt\r\nMột người tin tưởng còn một người giấu diếm.\r\nTiếc rằng tình yêu không hề giống như phép toán một cộng một bằng hai.\r\nBạn đã bao giờ nghe nói tới một vụ án liên quan đến những mối tình cắt không đứt, càng gỡ càng rối như vậy không?\r\nNhưng có lẽ, trong mối tình đó, chỉ cần một người bằng lòng quay đầu, nhìn về phía sau, có lẽ tất cả sẽ là bình minh tươi sáng và có lẽ ngày hôm nay… mọi chuyện đều đã khác…\r\n', 136, 'Lã Diệc Hàm , Thúy Nguyễn', '2016'),
(27, 'Người Là Biển Rộng, Kẻ Là Mặt Trời', 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a---ng_i-l_-bi_n-r_ng-k_-l_-m_t-tr_i.jpg', 'Truyện bắt đầu bằng tin nhắn “Chúng ta chia tay nhau đi” – báo hiệu một tình yêu xa đến hồi kết thúc. Nhưng lạ lùng thay họ đã kiên trì cùng nhau hơn ba năm, tuy cách nhau một nghìn năm trăm cây, hai tiếng bay, bảy tiếng tàu nhanh hay hai mươi bốn tiếng tàu chậm nhưng trong lòng họ luôn có nhau. Vậy vì sao cái tin nhắn ít mong đợi nhất ấy lại gửi đến Hạ Sơ Nhất vào một ngày tồi tệ nhất của cô?', 136, 'Hạ Chí ', '2021'),
(28, 'Gió Nam Hiểu Lòng Tôi', 'https://cdn0.fahasa.com/media/catalog/product/g/i/gionamhieulongtoi.jpg', '“Bạn có tin rằng một người đã chết có thể tồn tại bằng một phương thức nào đó hay không?”\r\nSài Diễm là một luật sư xinh đẹp, tài năng và có cá tính mạnh mẽ. Cô có người bạn thanh mai trúc mã là một bác sĩ nha khoa hài hước và “đáng ghét” Trần Vị Nam. Sài Diễm không hiểu nổi mình sao hồi còn đi học lại đem lòng thầm thích anh được. Lên đại học, sau khi chiến tranh lạnh với Trần Vị Nam, cô viết vào tình sử của mình những trang tình cảm với Trì Thu Thành, một trợ lí huấn luyện viên Taekwondo.\r\nKhông may sau đó, một tai nạn xảy ra cướp đi mạng sống của Trì Thu Thành. Cuộc đời Sài Diễm cũng vì thế mà xảy ra những điều không tưởng. Trải qua những nghi kị, hiểu lầm và biến cố lớn cuối cùng Sài Diễm và Trần Vị Nam cũng nhận ra được tình yêu dành cho nhau và bên nhau thắm thiết.\r\nĐúng lúc hai người hạnh phúc nhất thì người bạn trai năm xưa – Trì Thu Thành, người tưởng chừng đã chết chợt xuất hiện trở lại.\r\nTình bạn, tình yêu và chính cả bản thân cô dường như đều rơi vào vòng xoáy của một âm mưu nào đó. Làm sao Sài Diễm vượt qua được tất cả những khó khăn và âm mưu đen tối xảy đến ? Tình bạn liệu có luôn bên cạnh cô? Tình yêu liệu có luôn sát cánh cùng cô ? Và chính bản thân cô liệu có tự mình tìm ra câu trả lời từ trong những mơ hồ? Sau tất cả, bầu trời Kỳ Nam vẫn xoay vần, Đông tàn, Hạ lại sang.', 136, 'Ngô Đồng Tư Ngữ', '2020'),
(29, 'Đáp Án Của Thời Gian', 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia_dapanthoigian.jpg', 'Chúng ta ai cũng từng phải hứng chịu những trận mưa yên ắng trên biển rộng, phải lớn lên theo cách khắc nghiệt và đau lòng. Nhưng sau cùng, mọi điều xảy ra trong quá khứ sẽ tạo thành chúng ta của hiện tại. Và chẳng một ai có quyền năng quay ngược thời gian, việc mà chúng ta làm chỉ có thể là cố gắng sống tốt, chờ đợi đáp án của nó. Bởi vì thời gian tuy chưa từng một lần lên tiếng, nhưng sẽ là đáp án cho tất cả.\r\nĐây không phải là một cuốn sách bạn có thể đọc trong một buổi tối, cũng không phải loại tiểu thuyết bạn có thể đọc lướt cho xong. Thay vào đó bạn hãy nhâm nhi cuốn sách này tựa một tách trà chiều, bởi sau chút đắng chát chính là vị ngọt nơi đầu lưỡi. Điều này giống như sau khi kiên nhẫn chờ đợi, thời gian sẽ tìm mọi cách mang đến cho bạn một đáp án mà thôi.', 136, 'Lư Tư Hạo', '2021'),
(39, 'Uống Nhầm Một Ánh Mắt, Cơn Say Theo Cả Đời', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935212353144.jpg', 'Đây là một câu chuyện ấm áp, kể về một cô gái mù chơi đàn tranh được một nhiếp ảnh gia nổi tiếng theo đuổi. Tình yêu của họ không mấy sóng gió, Cố Sương Chi ban đầu còn hơi băn khoăn vì sợ Nguyễn Thanh Ngôn chỉ phải lòng trong thời gian ngắn ngủi, sợ anh không lường trước cuộc sống bất tiện của một người mù, càng sợ một khi đã mở cửa trái tim rồi đến lúc mất đi sẽ vô cùng đau đớn. Nhưng Nguyễn Thanh Ngôn cũng là một chàng trai khá lạ lùng, anh thà cô đơn chứ không vì cô đơn mà tìm ai đó yêu tạm. Nếu có người làm anh rung động, anh sẽ chung thủy đến tận cùng với người ấy. Và tình cờ làm sao, vị nhiếp ảnh gia ngoài nóng trong lạnh này bắt gặp một cô gái mảnh mai đeo hộp đàn tranh đứng trong vườn trường, ánh mắt mơ màng khó diễn tả khiến anh không thể kiềm chế mà đưa máy ảnh lên “tách” một tấm. Ánh mắt ấy đã khiến anh thẫn thờ, mất ngủ rồi theo anh ngày đêm khiến anh mãi nghĩ đến cô gái hơi kênh kiệu đó. Càng tiếp xúc anh càng thấy cô gái khiếm thị ấy rất lạc quan, trong trẻo như ánh nắng ban mai, dịu dàng nhưng rất kiên định.', 136, 'Lạc Vi Gian Mỗ Mỗ', '2020'),
(40, 'Hai Vạn Dặm Dưới Đáy Biển', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_235266_1.jpg', 'Trong chuyến săn tìm một sinh vật lạ lùng mới xuất hiện ở biển khơi, đoàn thám hiểm của giáo sư Pierre Aronnax bất ngờ bị“con cá\" khổng lồ đó nuốt vào bụng. Nhưng cũng nhờ thế, họ đã được chu du khắp các đại dương cùng thuyền trưởng Nemo trong chiếc tàu ngầm Nautilus. Những phát minh kì lạ, những cuộc chạm trán hiểm nguy, những bí mật li kì... tất cả đã tạo nên một chuyến phiêu lưu vô cùng thú vị.\r\nQua cuốn tiểu thuyết viễn tưởng cực kì hấp dẫn của Jules Verne, người đọc cảm nhận được ngọn lửa nhiệt tình không bao giờ tắt trên con đường khám phá thiên nhiên, tìm tòi khoa học và theo đuổi tự do chân chính. Chính điều đó đã góp phần giúp Hai Vạn Dặm Dưới Biển làm say lòng hàng triệu độc giả trên khắp thế giới từ hàng trăm năm nay.', 146, 'Jules Verne', '2020'),
(41, 'Tam Quốc Diễn Nghĩa', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_40337.jpg', 'Truyện kiếm hiệp, lịch sử Tam quốc Diễn Nghĩa lấy bối cảnh vào những năm cuối triều đại nhà Hán, sự suy yếu của của triều đình làm cho xã hội Trung Hoa rơi vào cảnh náo loạn. Vua tin dung hoạn quan khinh rẻ hiền tài, đam mê tửu sắc bỏ quên chính sự nên triều đình ngày càng đổ nát, cuộc sống người dân ngày càng lần than, cực khổ, giặc cướp nổi lên khắp nơi. Và nổi lên trong đó là loạn đảng khăn Vàng của anh em nhà Trương Giác với mấy vạn đồ đệ theo hầu. Loạn thế xuất anh hùng, nhiều hào kiệt bắt đầu lộ diện giúp nước nhà như ba anh em Lưu Bị, Quan Vũ, Trường Phi, quan Thừa ở Hạ Phi tên Tôn Kiên, Quan Kỵ đô uý Tào Tháo… Được sự giúp sức, cuối cùng triều đình cũng đánh tan được loạn đảng. Tuy nhiên, nhà vua vẫn ngựa quen đường cũ, bọn hoạn quan vẫn lộng hành. Thứ sử Tây Lương là Đổng Trác nhân cơ hội này đã phế truất vua cũ, lập vua mới rồi tự phong cho mình làm tướng quốc nắm hết quyền hành. Hành vi tàn bạo, lộng quyền của Đổng Trác khiến các quan vô cùng phẫn nổ. Thứ sử các châu, quận dẫn đầu là Kỳ Hương Hầu Viên Thiệu hội quân tiến đánh nhưng do nội bộ bất hoà nên quân đội cũng tan rã.', 146, 'La Quán Trung', '2011'),
(42, 'Thần Thoại Hy Lạp', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_33679.jpg', 'Ngay trong thời cổ đại, thần thoại Hy Lạp đã “hóa thân” thành thần thoại La Mã. Sự chế biến này của nền văn hóa La Mã gần với nguyên mẫu như sao chép đến nỗi khoa thần thoại học hầu như không có sự phân biệt giữa những vị thần Hy Lạp được đổi tên Latinh với những vị thần Hy Lạp chính cống. Người ta vẫn thường dùng Jupiter (Latinh) đề chỉ Zeus (Hy Lạp), Vénus để chỉ Aphrodite, Minerve để chỉ Athéna, Mars để chỉ Ares và ngược lại.\r\nNói như thế không có nghĩa là thần thoại La Mã chỉ là sự chế biến lại thần thoại Hy Lạp. Thần thoại La Mã cũng có một số sự tích, truyền thuyết về vị thần này, thần khác mang tính chất riêng biệt của mình. Nhưng nhìn chung cái vốn thần thoại gọi là đích thực La Mã ấy không lấy gì làm phong phú, đáng được liệt hạng.', 146, 'Phan Ngọc biên soạn', '2018'),
(43, 'Suối Nguồn', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180164_1_43_1_57_1_4_1_2_1_210_1_29_1_98_1_25_1_21_1_5_1_3_1_18_1_18_1_45_1_26_1_32_1_14_1_2215.jpg', 'Câu chuyện không là dòng chảy êm đềm như tên gọi, mà là một hành trình dài của tâm tưởng, của những cuộc đấu tranh giữa dòng đời khắc nghiệt để kiếm tìm và chứng minh một chân lý sống. Nhân vật chính là Howard Roark - một kiến trúc sư vào đời không có tấm bằng đại học. Anh đã bị đuổi ra khỏi trường kiến trúc chỉ vì không đồng ý thay thế chân lý bằng ý kiến của số đông người. Trong khi cuộc sống đầy rẫy những toan tính, đua chen và con người luôn phải biết cúi đầu để có thể tồn tại thì Roark lại đi ngược với dòng chảy đó.\r\nNhững tòa nhà của Howard Roark xây dựng luôn khiến người ta phải tìm đến ngắm nghía, cho dù sau đó bật ra những tiếng dè bỉu, chê bai. Suy nghĩ tồn tại trong số đông người thì không thể gọi là lập dị, vì thế chẳng ai dám tách ra khỏi cái số đông ấy để đến bên cạnh Howard Roark. Người ta hả hê khi lật đổ được Roark, nhưng lại hằn học khi không thể phủ nhận rằng Roark là một thiên tài.\r\nHoward Roark đã “chịu đựng đến tận cùng nỗi đau”, để rồi cuối cùng anh đã chiến thắng. Một người đã bị búa rìu dư luận lên án vì những kiểu kiến trúc “khác người”, “bỏ đi” cuối cùng đã đứng trên giàn giáo chỉ huy một công trình kiến trúc vĩ đại nhất nước Mỹ. “Ở đó, chỉ còn đại dương, bầu trời và dáng hình của Howard Roark”.\r\nCuộc sống này không có ngõ cụt, chỉ có những con đường dẫn đến thành công. Giống như mọi dòng chảy đều có mạch ngầm và đại dương vẫn phải bắt đầu từ những dòng suối. Bước vào một ngõ hẹp không có nghĩa là thất bại, mà quan trọng là ta đủ bản lĩnh để nhìn thấy những bước chân mình.', 146, 'Ayn Rand', '2018'),
(44, 'Đất Rừng Phương Nam', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935244872002.jpg', 'Câu chuyện mượn hình ảnh một cậu bé bị lưu lạc trong thời kỳ kháng chiến chống Pháp ở Nam bộ để giới thiệu \"Đất rừng Phương Nam\". Nơi đó, một vùng đất vô cùng giàu có, hào phóng và hùng vĩ với những con người trung hậu, trí dũng, một lòng một dạ theo kháng chiến.\r\n\"Đất rừng phương Nam\" của Đoàn Giỏi như một xã hội của miền sông nước Tây Nam bộ thu nhỏ. Bởi ở nơi đó, người đọc đã tìm thấy hình ảnh người dân của vùng đất phương Nam từ sông Tiền, sông Hậu trải dài đến Kiên Giang - Rạch Giá, rồi xuống tận rừng U Minh, sau đó dừng lại ở Năm Căn Cà Mau.', 146, 'Đoàn Giỏi', '2022'),
(45, 'Hộp Sách: Tây Du Ký (Tái Bản 2018)', 'https://cdn0.fahasa.com/media/catalog/product/h/o/hop_sach_tay_du_ky_tai_ban_2018_1_2020_05_26_15_19_09.jpg', 'Tây du ký là đỉnh cao sáng tạo trong lịch sử phát triển về tiểu thuyết cổ điển của Trung Quốc). Chủ nghĩa lãng mạn tích cực là đặc sắc nghệ thuật chủ yếu của nó. Nó đã kế thừa và phát huy truyền thống tốt đẹp của thần thoại cổ đại Trung Quốc, đã thể hiện sức sáng tạo vĩ đại và sức tưởng tượng phong phú của dân tộc Trung Hoa. \"Chủ nghĩa lãng mạn là cơ sở của thần thoại\" (lời của M.Goocki) Tây du ký chính là một bộ tiểu thuyết thần thoại trường thiên vĩ đại, lãng mạn tích cực. Thông qua hình thức ảo tưởng thần kỳ và nội dung bắt nguồn ở hiện thực mà cao hơn hiện thực. Tây du ký đã phản ánh đầy đủ, khúc chiết, phức tạp lý tưởng cao quý và đời sống hiện thực của nhân dân.\r\n... Tây du ký có nội dung bắt nguồn ở hiện thực mà lại cao hơn hiện thực. Nó thông qua hình tượng nhân vật mà biểu hiện ra. Hình tượng nhân vật của Tây du ký đều có đầy đủ ý nghĩa hiện thực, nhưng đều không na ná hoặc đơn giản bắt chước hiện thực, cũng không khái quát chung chung mà cao hơn hiện thực nhiều. ở đây thiện thì càng thiện, ác thì càng ác, đều từ trên phương hướng của bản thân mình vượt cao hơn hiện thực mà đi vào phạm vi ảo tưởng hóa, lý tưởng hóa.\r\nTrong Tây du ký hình thức kỳ ảo kết hợp với nội dung hiện thực cực lớn, đã đạt tới sự thống nhất khéo léo. Chính vì thế sự tưởng tượng của Tây du ký mới được biểu hiện ra có ý nghĩa biết bao, khỏe khoắn tốt đẹp, sinh động và thú vị biết bao! Hợp tình hợp lý khiến người tin phục và sáng ngời trí tuệ biết bao!', 146, 'Ngô Thừa Ân', '2018'),
(46, 'Heidi', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_40336.jpg', 'Tác phẩm kể về cô bé Heidi khi 5 tuổi đã mồ côi cả cha lẫn mẹ, sống cùng bà ngoại và người dì. Khi bà ngoại mất, vì vội vã đi kiếm việc làm mới, người dì ích kỷ đã gửi Heidi đến cho ông nội của cô bé, đang sống cô độc, xa lánh loài người trên vùng núi Alm (hay dãy Apls Thụy Sĩ).\r\nAi cũng ái ngại cho Heidi khi phải sống với ông già lập dị và cục cằn ấy. Nhưng ai ngờ được rằng, Heidi bé bỏng với sự thơ ngây, vui vẻ và tấm lòng nhân hậu tự nhiên, chẳng những đã giúp ông nội tìm lại lòng yêu cuộc sống, mà còn mang đến nhiều đổi thay kỳ diệu cho cuộc sống của bao người quanh em.\r\nThiên nhiên Thụy Sĩ với những cánh đồng hoa rực rỡ, thung lũng mênh mông và ráng chiều rực đỏ trên những ngọn núi thanh tĩnh ngàn đời của vùng núi Alm, cùng câu chuyện tươi sáng về cô bé Heidi sẽ sưởi ấm cả những trái tim khô cằn nhất.', 146, 'Johanna Spyri, Elena Selivanova', '2020'),
(47, 'Đồi Gió Hú', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_48045.jpg', 'Đồi Gió Hú, câu chuyện cổ điển về tình yêu ngang trái và tham vọng chiếm hữu, cuốn tiểu thuyết dữ dội và bí ẩn về Catherine Earnshaw, cô con gái nổi loạn của gia đình Earnshaw, với gã đàn ông thô ráp và điên rồ mà cha cô mang về nhà rồi đặt tên là Heathcliff, được trình diễn trên cái nền đồng truông, quả đồi nước Anh cô quạnh và đơn sơ không kém gì chính tình yêu của họ. Từ nhỏ đến lớn, sự gắn bó của họ ngày càng trở nên ám ảnh. Gia đình, địa vị xã hội, và cả số phận rắp tâm chống lại họ, bản tính dữ dội và ghen tuông tột độ cũng huỷ diệt họ, vậy nên toàn bộ thời gian hai con người yêu nhau đó đã sống trong thù hận và tuyệt vọng, mà cái chết chỉ có ý nghĩa khởi đầu. Một khởi đầu mới để hai linh hồn mãnh liệt đó dược tự do tái ngộ, Khi những cơn gió hoang vắng và điên cuồng tràn về quanh các lâu đài trong Đồi gió hú...', 146, 'Emily Dronte', '2020'),
(48, 'Alice Ở Xứ Sở Diệu Kỳ', 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_223376.jpg', 'Alice ở xứ sở diệu kỳ là cuốn tiểu thuyết thiếu nhi nổi tiếng của nhà văn người Anh, Lewis Carroll. Ngay khi ra đời vào năm 1865, cuốn truyện đã được đông đảo bạn đọc cả trẻ em lẫn người lớn yêu mến. Bảy năm sau, Lewis Carroll cho ra mắt phần tiếp theo, Alice ở xứ sở trong gương, và ngay lập tức những nhân vật như cặp anh em Tweedledum, Hậu Đỏ, Hậu Trắng…của phần này cũng trở nên nổi tiếng không kém Thỏ Trắng, Sâu bướm, nữ Công tước hay Vua Cơ và Hậu Cơ ở phần trước\r\nNhờ sức hấp dẫn ấy mà trong hơn 130 năm qua, hàng loạt tác phẩm nghệ thuật từ văn học, hội họa, truyền hình, điện ảnh cho đến âm nhạc, game và cả opera đã ra đời dựa trên những cuộc phiêu lưu của cô bé Alice. Và cho đến nay, hai tác phẩm giàu sức tưởng tượng này vẫn là niềm cảm hứng mạnh mẽ cho những người yêu thích văn chương và sáng tạo.', 146, 'Lewis Carroll, Hoàng Đậu Xanh', '2021'),
(49, 'Phù Thủy Xứ Oz', 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935095627356.jpg', 'Phù thủy xứ OZ kể về một chuyến mạo hiểm đầy thú vị và kịch tính. Nhân vật chính là cô bé Dorothy sống ở bang Kansas nước Mỹ. Một ngày, cô và chú chó nhỏ Toto bị một con lốc xoáy dữ dội cuốn bay đến xứ sở OZ. Để tìm cách trở về nhà, quay về bên cạnh những người thân yêu, Dorothy đã phải trải qua không ít khó khăn, nguy hiểm. Trong quá trình đó, cô bé lần lượt gặp được Bù Nhìn – người cần một bộ não, Người Thiếc – người cần một trái tim và một con sư tử nhát gan cần lòng dũng cảm. Để thực hiện mơ ước của chính mình, mỗi người họ đã cùng nhau vượt qua biết bao thử thách gian nan, đối diện với những cuộc gặp gỡ ly kỳ. Câu chuyện vô cùng sinh động, đặc sắc, tình tiết hấp dẫn tuyệt với, \"Thời báo New York\" đã từng ca ngợi đây là \"tác phẩm vượt xa tất cả những câu chuyện thiếu nhi đương thời, là một tác phẩm vô cùng xuất sắc\".', 146, 'L Frank Baum', '2020');
